import React, {useState,useEffect} from 'react';
import { StyleSheet, Text, View, Button,FlatList,TouchableOpacity } from 'react-native';
import vjezbeServer from '../src/services/vjezbe'
import { WebView } from 'react-native-webview';
const VjezbeKategorije = (props) => {

  const dioTijela=props.navigation.getParam('katNaziv')
  const [vjezbe, postaviVjezbe]=useState([])
  useEffect( ()=>{
    console.log("effect hook");
    vjezbeServer.dohvatiSve().then((response) => {
      console.log("Podaci ucitani");
      postaviVjezbe(response.data)
    })
  })

  const objekt=vjezbe.filter(rec => rec.kategorija===dioTijela)
  
  const prikaziVjezbe=(recept) =>
  {
    return (
      <View>
        <Text> {recept.item.naziv}</Text>
        <WebView
        style={{ marginTop: 20, width: 320, height: 230 }}
        javaScriptEnabled={true}
        domStorageEnabled={true}
        source={{ uri: "https://www.youtube.com/embed/LueYlLt1l9M" }}
      />
      </View>
    )
  }
 return (
    <View style={stil.ekran}>
       <FlatList data={objekt} renderItem={prikaziVjezbe} />
      
      <Button title= 'PrikaÅ¾i samo jedan video' onPress={()=> { props.navigation.navigate('Detalji')  }} />
      <Button title= 'Natrag' onPress={()=> { 
        props.navigation.navigate('SveKategorije') }}
      />

<Button title= 'Odjava' 
onPress={()=> { props.navigation.navigate('Login') }}/>
    </View>
  );
};

VjezbeKategorije.navigationOptions= (navigationData) => {
  const kat=navigationData.navigation.getParam('katNaziv')
  return{ headerTitle:kat }
  }
const stil = StyleSheet.create({
  ekran: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
});

export default VjezbeKategorije
