import React, {useState,useEffect} from 'react';
import { StyleSheet, Text, View, Button } from 'react-native';
import vjezbeServer from '../src/services/vjezbe'

const VjezbeKategorije = (props) => {
  const dioTijela=props.navigation.getParam('katNaziv')
  
  const [vjezbe, postaviVjezbe]=useState([])

  useEffect( ()=>{
    console.log("effect hook");
    vjezbeServer.dohvatiSve().then((response) => {
      console.log("Podaci ucitani");
      postaviVjezbe(response.data)
    })
  })
  const kategorije = [...new Set(vjezbe.map(v => v.kategorija))]
  
  const renderGrid = (kategorije) => {
    return (
      <TouchableOpacity style={stil.gridElement}
      onPress={() => {
        props.navigation.navigate({
          routeName: 'JednaKategorija',
          params: {
            katNaziv: kategorije.item
          }
        })
      }}>
        <View >
        <Text>{kategorije.item}</Text>
      </View>
      </TouchableOpacity>
    )
  }

  
  const odabranaKat=vjezbeServer.find(kat=> kat.kategorija===kategorija)
  return (
    <View style={stil.ekran}>
      <Text> Kategorija je: {dioTijela} </Text>

      
      <Button title= 'PrikaÅ¾i samo jedan video' onPress={()=> { props.navigation.navigate('Detalji')  }} />
      <Button title= 'Natrag' onPress={()=> { 
        props.navigation.navigate('SveKategorije') }}
      />

<Button title= 'Odjava' 
onPress={()=> { props.navigation.navigate('Login') }}/>
    </View>
  );
};

const stil = StyleSheet.create({
  ekran: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
});

export default VjezbeKategorije
